import React, { useState } from "react";
import "./CSS/LoginSignup.css";

export const LoginSignup = () => {
  const [passwordVisible, setPasswordVisible] = useState(false);

  const togglePasswordVisibility = () => {
    setPasswordVisible(!passwordVisible);
  };

  const showForgotPasswordModal = () => {
    // Implement modal or navigate to the "Forgot Password" page
    alert("Show Forgot Password Modal or navigate to the page");
  };

  return (
    <div className="loginsignup">
      <div className="loginsignup-container">
        <h1>Sign Up</h1>
        <div className="loginsignup-fields">
          <input type="text" placeholder="Your Name" />
          <input type="email" placeholder="Email Address" />
          <input type="phonenumber" placeholder="Phone Number" />
          <div className="password-input-container">
            <input
              type={passwordVisible ? "text" : "password"}
              placeholder="Password"
            />
            {/* Add the Eye icon to toggle password visibility */}
            <span
              className={`toggle-password ${passwordVisible ? "visible" : ""}`}
              onClick={togglePasswordVisibility}
            ></span>
          </div>
          <input
            type={passwordVisible ? "text" : "password"}
            placeholder="Confirm Password"
          />
        </div>
        <button>Sign-up</button>
        <p className="loginsignup-login">
          Already have an account? <span>Login here</span>
          <a href="#" onClick={showForgotPasswordModal}>
            Forgot Password?
          </a>
          <div className="loginsignup-agree">
            <input type="checkbox" name="" id="" />
            <p>By continuing, I agree to the terms of use and privacy policy</p>
          </div>
        </p>
      </div>
    </div>
  );
};

export default LoginSignup;
