import React, { useState } from "react";
import "./Navbar.css";
import logo from "../Assets/logo.png";
import cart_icon from "../Assets/cart_icon.png";
import { Link, Outlet } from "react-router-dom";
import { useContext } from "react";
import { ShopContext } from "../../Context/ShopContext";

const DokoNavbar = () => {
  const [menu, setMenu] = useState("shop");
  const { getTotalCartItems } = useContext(ShopContext);

  return (
    <>
      <div className="navbar">
        <div className="nav-logo">
          <img src={logo} alt="" />
          <p>DOKO</p>
        </div>
        <ul className="nav-menu">
          <li
            onClick={() => {
              setMenu("shop");
            }}
          >
            <Link to="/">Shop</Link>
            {menu === "shop" ? <div className="active-indicator" /> : null}
          </li>
          <li
            onClick={() => {
              setMenu("men");
            }}
          >
            <Link to="/men">Men</Link>
            {menu === "men" ? <div className="active-indicator" /> : null}
          </li>
          <li
            onClick={() => {
              setMenu("women");
            }}
          >
            <Link to="/women">Women</Link>
            {menu === "women" ? <div className="active-indicator" /> : null}
          </li>
          <li
            onClick={() => {
              setMenu("kids");
            }}
          >
            <Link to="/kids">Kids</Link>
            {menu === "kids" ? <div className="active-indicator" /> : null}
          </li>
        </ul>
        <div className="nav-login-cart">
          <Link to="/login">
            <button>Login</button>
          </Link>
          <Link to="/cart">
            <img src={cart_icon} alt="" />
          </Link>
          <div className="nav-cart-count">{getTotalCartItems()}</div>
        </div>
      </div>
      <Outlet />
    </>
  );
};

export default DokoNavbar;
