import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css"; // Import Bootstrap CSS
import { Container, Form, Button, FormGroup, FormCheck } from "react-bootstrap"; // Import Bootstrap components
import "./CSS/LoginSignup.css";

const LoginSignup = () => {
  const [passwordVisible, setPasswordVisible] = useState(false);
  const [confirmPasswordVisible, setConfirmPasswordVisible] = useState(false);

  const togglePasswordVisibility = (field) => {
    if (field === "password") {
      setPasswordVisible(!passwordVisible);
    } else if (field === "confirmPassword") {
      setConfirmPasswordVisible(!confirmPasswordVisible);
    }
  };

  return (
    <Container className="loginsignup">
      <div className="loginsignup-container">
        <h1>Sign Up</h1>
        <Form className="loginsignup-fields">
          <FormGroup>
            <Form.Control type="text" placeholder="Your Name" />
          </FormGroup>
          <FormGroup>
            <Form.Control type="email" placeholder="Email Address" />
          </FormGroup>
          <FormGroup>
            <Form.Control type="tel" placeholder="Phone Number" />
          </FormGroup>

          <FormGroup className="password-container">
            <Form.Control
              className={`password-input ${passwordVisible ? "visible" : ""}`}
              type={passwordVisible ? "text" : "password"}
              placeholder="Password"
            />
            <span
              className={`toggle-password ${passwordVisible ? "visible" : ""}`}
              onClick={() => togglePasswordVisibility("password")}
            >
              üëÅÔ∏è
            </span>
          </FormGroup>
          <FormGroup className="confirm-password-container">
            <Form.Control
              className={`password-input ${
                confirmPasswordVisible ? "visible" : ""
              }`}
              type={confirmPasswordVisible ? "text" : "password"}
              placeholder="Confirm Password"
            />
            <span
              className={`toggle-password ${
                confirmPasswordVisible ? "visible" : ""
              }`}
              onClick={() => togglePasswordVisibility("confirmPassword")}
            >
              üëÅÔ∏è
            </span>
          </FormGroup>
          <Button variant="primary" type="submit">
            Sign-up
          </Button>
        </Form>
        <p className="loginsignup-login">
          Already have an account? <span>Login here</span>
        </p>
        <div className="loginsignup-agree">
          <FormCheck
            type="checkbox"
            label="By continuing, I agree to the terms of use and privacy policy"
          />
        </div>
      </div>
    </Container>
  );
};

export default LoginSignup;
